import React, { useState } from 'react';
import Login from './components/Login';
import Signup from './components/Signup';
import './App.css';

function App() {
  const [isLogin, setIsLogin] = useState(true); // State để chuyển đổi giữa login và signup

  const handleToggle = () => {
    setIsLogin(!isLogin); // Chuyển đổi giữa login và signup
  };

  return (
    <div className="App">
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <div className="form-container">
        <div className="form-box">
          {isLogin ? <Login /> : <Signup />}
        </div>
      </div>
      <div className="button-container">
        <button onClick={() => setIsLogin(false)}>Switch to Sign Up</button>
        <button onClick={() => setIsLogin(true)}>Switch to Login</button>
      </div>
    </div>
  );
}

export default App;
