import React, { useState } from 'react';
import Login from './components/Login';
import Signup from './components/Signup';
import './App.css';

function App() {
  const [isLogin, setIsLogin] = useState(true); // State để chuyển đổi giữa login và signup

  const handleToggle = () => {
    setIsLogin(!isLogin); // Chuyển đổi giữa login và signup
  };

  return (
    <div className="App">
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      {isLogin ? <Login /> : <Signup />}
      <button onClick={handleToggle}>
        {isLogin ? 'Switch to Sign Up' : 'Switch to Login'}
      </button>
    </div>
  );
}

export default App;
